mapper.select:
SELECT BOARD.*, PDS.* 
FROM BOARD LEFT OUTER JOIN PDS ON (BOARD.BO_NO = PDS.BO_NO)
WHERE BOARD.BO_NO = #{bo_no}

mapper.update:
UPDATE BOARD
SET BO_HIT = BO_HIT+1
WHERE BO_NO = #{bo_no}

mapper.insert:

	SELECT BOARD_SEQ.NEXTVAL FROM DUAL

INSERT INTO board (
    BO_NO,    BO_WRITER,    BO_PASS,    BO_IP,
    BO_MAIL,    BO_TITLE,    BO_CONTENT,    BO_DATE

    ,  BO_PARENT

) VALUES (
    #{bo_no},    #{bo_writer},    #{bo_pass},    #{bo_ip},
    #{bo_mail},    #{bo_title},    #{bo_content},    sysdate	

    , #{bo_parent,jdbcType=NUMERIC}

)

mapper.update:
UPDATE BOARD
SET BO_RCMD = BO_RCMD+1
WHERE BO_NO = #{bo_no}
